		this.setGridLinesVisible(false);
		this.setStyle("-fx-background-color: darkgreen");
		this.setAlignment(Pos.CENTER);

		Label lblOverskrift = new Label("Velkommen til Aarhus Bryghus' KasseApparatSystem");
