		GridPane.setHalignment(lblOverskrift, HPos.CENTER);
		lblOverskrift.setStyle("-fx-text-fill: white; -fx-font-size: 30px");

		Label lblUnderrubrik = new Label("Brug fanerne til at registrere salg og oprette produkter, produktgrupper og prislister.");
